import { GoogleGenAI } from "@google/genai";

interface ArticleAnalysis {
  summary: string;
  category: string;
  bias: 'left' | 'right' | 'neutral';
  biasConfidence: number;
  sentimentScore: number;
  emotionalTone: 'positive' | 'negative' | 'neutral';
}

class AIService {
  private gemini: GoogleGenAI;

  constructor() {
    const apiKey = process.env.GEMINI_API_KEY || '';
    if (!apiKey) {
      console.warn('GEMINI_API_KEY not found in environment variables');
    }

    this.gemini = new GoogleGenAI({ apiKey });
  }

  async analyzeArticle(title: string, content: string): Promise<ArticleAnalysis> {
    if (!process.env.GEMINI_API_KEY) {
      // Return default analysis if no API key
      return this.getDefaultAnalysis(title, content);
    }

    try {
      const systemPrompt = `Analyze the news article and return structured JSON with these fields:
- summary: Key points in 2-3 sentences
- category: general, politics, technology, health, finance, or sports
- bias: left, right, or neutral
- biasConfidence: 0-1 confidence score
- sentimentScore: -1 (negative) to 1 (positive)
- emotionalTone: positive, negative, or neutral

Title: ${title}
Content: ${content.substring(0, 2000)}`;

      const response = await this.gemini.models.generateContent({
        model: "gemini-2.5-flash",
        config: {
          systemInstruction: systemPrompt,
          responseMimeType: "application/json",
          responseSchema: {
            type: "object",
            properties: {
              summary: { type: "string" },
              category: { type: "string" },
              bias: { type: "string" },
              biasConfidence: { type: "number" },
              sentimentScore: { type: "number" },
              emotionalTone: { type: "string" }
            },
            required: ["summary", "category", "bias", "biasConfidence", "sentimentScore", "emotionalTone"]
          }
        },
        contents: `Analyze this news article: Title: ${title}\n\nContent: ${content.substring(0, 2000)}`
      });

      const analysisText = response.text;
      if (!analysisText) {
        throw new Error("Empty response from Gemini");
      }

      const analysis = JSON.parse(analysisText);

      // Validate and sanitize response
      return {
        summary: analysis.summary || this.generateFallbackSummary(content),
        category: this.validateCategory(analysis.category),
        bias: this.validateBias(analysis.bias),
        biasConfidence: Math.max(0, Math.min(1, analysis.biasConfidence || 0.5)),
        sentimentScore: Math.max(-1, Math.min(1, analysis.sentimentScore || 0)),
        emotionalTone: this.validateEmotionalTone(analysis.emotionalTone),
      };
    } catch (error) {
      console.error('AI analysis error:', error);
      return this.getDefaultAnalysis(title, content);
    }
  }

  private getDefaultAnalysis(title: string, content: string): ArticleAnalysis {
    return {
      summary: this.generateFallbackSummary(content),
      category: this.inferCategoryFromText(title + ' ' + content),
      bias: 'neutral',
      biasConfidence: 0.3,
      sentimentScore: 0,
      emotionalTone: 'neutral',
    };
  }

  private generateFallbackSummary(content: string): string {
    // Simple fallback: take first 2 sentences or first 200 chars
    const sentences = content.split(/[.!?]+/).filter(s => s.trim().length > 10);
    if (sentences.length >= 2) {
      return sentences.slice(0, 2).join('. ').trim() + '.';
    }
    return content.substring(0, 200).trim() + '...';
  }

  private inferCategoryFromText(text: string): string {
    const lowerText = text.toLowerCase();

    if (lowerText.includes('government') || lowerText.includes('election') || lowerText.includes('parliament') || lowerText.includes('policy')) {
      return 'politics';
    }
    if (lowerText.includes('technology') || lowerText.includes('ai') || lowerText.includes('software') || lowerText.includes('startup')) {
      return 'technology';
    }
    if (lowerText.includes('health') || lowerText.includes('medical') || lowerText.includes('hospital') || lowerText.includes('disease')) {
      return 'health';
    }
    if (lowerText.includes('economy') || lowerText.includes('market') || lowerText.includes('finance') || lowerText.includes('gdp') || lowerText.includes('rupee')) {
      return 'finance';
    }
    if (lowerText.includes('cricket') || lowerText.includes('football') || lowerText.includes('sports') || lowerText.includes('match')) {
      return 'sports';
    }

    return 'general';
  }

  private validateCategory(category: string): string {
    const validCategories = ['politics', 'technology', 'health', 'finance', 'sports'];
    return validCategories.includes(category) ? category : 'general';
  }

  private validateBias(bias: string): 'left' | 'right' | 'neutral' {
    const validBias = ['left', 'right', 'neutral'];
    return validBias.includes(bias) ? bias as 'left' | 'right' | 'neutral' : 'neutral';
  }

  private validateEmotionalTone(tone: string): 'positive' | 'negative' | 'neutral' {
    const validTones = ['positive', 'negative', 'neutral'];
    return validTones.includes(tone) ? tone as 'positive' | 'negative' | 'neutral' : 'neutral';
  }

  async generateNewsArticle(category: string, topic?: string): Promise<{title: string, content: string, urlToImage?: string}> {
    if (!process.env.GEMINI_API_KEY) {
      throw new Error('Gemini API key not configured');
    }

    try {
      const prompt = topic 
        ? `Generate a realistic news article about ${topic} in the ${category} category.`
        : `Generate a realistic news article in the ${category} category about current trending topics.`;

      const systemPrompt = `Generate a news article with proper journalistic structure. Return JSON with title, content (300-500 words), and urlToImage. Include realistic quotes and details while maintaining professional news writing standards.`;

      const response = await this.gemini.models.generateContent({
        model: "gemini-2.5-flash",
        config: {
          systemInstruction: systemPrompt,
          responseMimeType: "application/json",
          responseSchema: {
            type: "object",
            properties: {
              title: { type: "string" },
              content: { type: "string" },
              urlToImage: { type: "string" }
            },
            required: ["title", "content"]
          }
        },
        contents: prompt
      });

      const articleText = response.text;
      if (!articleText) {
        throw new Error("Empty response from Gemini");
      }

      const article = JSON.parse(articleText);

      return {
        title: article.title || "Generated News Article",
        content: article.content || "Content generation failed.",
        urlToImage: article.urlToImage
      };
    } catch (error) {
      console.error('News generation error:', error);
      throw new Error(`Failed to generate news article: ${error}`);
    }
  }
}

export const aiService = new AIService();

server/services/newsService.ts.content:
import { storage } from '../storage';
import { aiService } from './aiService';
import { getArticleImage } from '../utils/imageMapping';
import type { InsertArticle } from '@shared/schema';

interface NewsAPIArticle {
  title: string;
  description: string;
  content: string;
  url: string;
  urlToImage: string;
  publishedAt: string;
  source: {
    name: string;
  };
  author: string;
}

interface NewsAPIResponse {
  articles: NewsAPIArticle[];
}

class NewsService {
  private readonly apiKey: string;
  private readonly baseUrl = 'https://newsapi.org/v2';
  private readonly indianNewsSourceUrls = [
    'https://www.thehindu.com',
    'https://www.ndtv.com', 
    'https://indianexpress.com',
    'https://www.business-standard.com'
  ];

  constructor() {
    this.apiKey = process.env.NEWS_API_KEY || process.env.NEWSAPI_KEY || '';
    if (!this.apiKey) {
      console.warn('NEWS_API_KEY not found in environment variables');
    }
  }

  private getDistributedSourceUrl(index: number): string {
    return this.indianNewsSourceUrls[index % this.indianNewsSourceUrls.length];
  }

  async fetchLatestNews(category?: string, country = 'in'): Promise<void> {
    if (!this.apiKey) {
      throw new Error('News API key not configured');
    }

    try {
      const url = new URL(`${this.baseUrl}/top-headlines`);
      url.searchParams.set('apiKey', this.apiKey);
      url.searchParams.set('country', country);
      url.searchParams.set('pageSize', '20');

      if (category) {
        url.searchParams.set('category', category);
      }

      const response = await fetch(url.toString());

      if (!response.ok) {
        throw new Error(`News API error: ${response.status} - ${response.statusText}`);
      }

      const data: NewsAPIResponse = await response.json();

      // Process each article with distributed URLs
      for (let index = 0; index < data.articles.length; index++) {
        try {
          await this.processAndStoreArticle(data.articles[index], index);
        } catch (error) {
          console.error('Error processing article:', data.articles[index].title, error);
        }
      }
    } catch (error) {
      console.error('Error fetching news:', error);
      throw error;
    }
  }

  private async processAndStoreArticle(newsArticle: NewsAPIArticle, index: number = 0): Promise<void> {
    // Check if article already exists
    const existingArticles = await storage.getArticles({ search: newsArticle.title, language: 'en' });
    if (existingArticles.some(a => a.url === newsArticle.url)) {
      return; // Skip duplicate
    }

    // Analyze the article with AI
    const analysis = await aiService.analyzeArticle(
      newsArticle.title,
      newsArticle.content || newsArticle.description || ''
    );

    // Create article object with distributed source URL
    const article: InsertArticle = {
      title: newsArticle.title,
      content: newsArticle.content || newsArticle.description || '',
      summary: analysis.summary,
      url: this.getDistributedSourceUrl(index),
      imageUrl: newsArticle.urlToImage || getArticleImage(analysis.category, newsArticle.title, newsArticle.content || newsArticle.description),
      source: newsArticle.source.name,
      author: newsArticle.author,
      category: analysis.category,
      publishedAt: new Date(newsArticle.publishedAt),
      politicalBias: analysis.bias,
      biasConfidence: analysis.biasConfidence,
      sentimentScore: analysis.sentimentScore,
      emotionalTone: analysis.emotionalTone,
      availableLanguages: [],
    };

    // Store in database
    await storage.createArticle(article);
  }

  async refreshAllCategories(): Promise<void> {
    const categories = ['general', 'business', 'health', 'science', 'sports', 'technology'];

    for (const category of categories) {
      try {
        await this.fetchLatestNews(category);
        // Add delay to respect API rate limits
        await new Promise(resolve => setTimeout(resolve, 1000));
      } catch (error) {
        console.error(`Error fetching ${category} news:`, error);
      }
    }
  }

  async generateNewsArticles(count: number = 5): Promise<void> {
    const categories = ['general', 'politics', 'technology', 'health', 'finance', 'sports'];

    console.log(`Generating ${count} new editorial articles...`);

    for (let i = 0; i < count; i++) {
      try {
        const category = categories[i % categories.length];
        const generatedArticle = await aiService.generateNewsArticle(category);

        // Analyze the generated article
        const analysis = await aiService.analyzeArticle(
          generatedArticle.title,
          generatedArticle.content
        );

        // Create article object with distributed source URL
        const article: InsertArticle = {
          title: generatedArticle.title,
          content: generatedArticle.content,
          summary: analysis.summary,
          url: this.getDistributedSourceUrl(i),
          imageUrl: generatedArticle.urlToImage || getArticleImage(analysis.category, generatedArticle.title, generatedArticle.content),
          source: 'Editorial Team',
          author: 'Staff Writer',
          category: analysis.category,
          publishedAt: new Date(),
          politicalBias: analysis.bias,
          biasConfidence: analysis.biasConfidence,
          sentimentScore: analysis.sentimentScore,
          emotionalTone: analysis.emotionalTone,
          availableLanguages: [],
        };

        // Store in database
        await storage.createArticle(article);
        console.log(`Generated article: ${generatedArticle.title}`);

        // Add delay between generations
        await new Promise(resolve => setTimeout(resolve, 1000));
      } catch (error) {
        console.error(`Error generating article ${i + 1}:`, error);
      }
    }
  }

  async generateAndRefresh(): Promise<void> {
    try {
      // Generate editorial content
      await this.generateNewsArticles(3);
    } catch (error) {
      console.error('Error generating editorial articles:', error);
    }

    // Then try to fetch from News API if available
    if (this.apiKey) {
      try {
        await this.refreshAllCategories();
      } catch (error) {
        console.error('Error fetching from News API:', error);
      }
    }
  }
}

export const newsService = new NewsService();

