                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="newspaper">ðŸ“° Newspaper</SelectItem>
                          <SelectItem value="broadcast">ðŸ“º Broadcast</SelectItem>
                          <SelectItem value="digital">ðŸ’» Digital</SelectItem>
                          <SelectItem value="agency">ðŸ¢ News Agency</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                  </div>

                  <Button 
                    onClick={handleAddSource} 
                    disabled={isSubmitting}
                    className="w-full"
                  >
                    {isSubmitting ? (
                      <>
                        <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                        Adding to Blockchain...
                      </>
                    ) : (
                      <>
                        <Plus className="w-4 h-4 mr-2" />
                        Add Verified Source
                      </>
                    )}
                  </Button>
                </div>
              </CardContent>
            )}
          </Card>
        )}

        {/* Verified Sources List */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              <span className="flex items-center gap-2">
                <ShieldCheck className="w-5 h-5" />
                Verified Sources ({verifiedSources.length})
              </span>
              <Button
                variant="outline"
                size="sm"
                onClick={refreshData}
                disabled={isLoading}
              >
                <RefreshCw className={`w-4 h-4 mr-2 ${isLoading ? 'animate-spin' : ''}`} />
                Refresh
              </Button>
            </CardTitle>
            <CardDescription>
              News sources verified on the Polygon Mumbai blockchain
            </CardDescription>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="flex items-center justify-center py-8">
                <Loader2 className="w-6 h-6 animate-spin mr-2" />
                Loading verified sources...
              </div>
            ) : verifiedSources.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                <Shield className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                <p>No verified sources found</p>
                <p className="text-sm">Add some sources to get started</p>
              </div>
            ) : (
              <div className="space-y-4">
                {verifiedSources.map((source, index) => (
                  <div
                    key={index}
                    className="flex items-center justify-between p-4 border rounded-lg hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors"
                  >
                    <div className="flex items-center gap-4">
                      <div className="text-2xl">
                        {getCategoryIcon(source.category)}
                      </div>
                      <div>
                        <h3 className="font-medium text-gray-900 dark:text-white">
                          {source.name}
                        </h3>
                        <p className="text-sm text-gray-500">{source.domain}</p>
                        <div className="flex items-center gap-2 mt-1">
                          <Badge
                            variant="outline"
                            className={getTrustScoreColor(source.trustScore)}
                          >
                            Trust Score: {source.trustScore}/100
                          </Badge>
                          <Badge variant="secondary" className="capitalize">
                            {source.category}
                          </Badge>
                        </div>
                      </div>
                    </div>

                    <div className="flex items-center gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => window.open(`https://${source.domain}`, '_blank')}
                      >
                        <ExternalLink className="w-4 h-4" />
                      </Button>

                      {isWalletConnected && currentAccount === contractInfo?.owner && (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleRemoveSource(source.domain)}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

